import Navbar from './navbar';
import './quiz.css';

export class Quiz {
    render() {
    this.state = {
        Value1 : 'Warm',
        Value2 : 'Cold',
        Value3 : 'Both'
        }

    function click() {
        this.setState({Value1 :'Milk Dairy', Value2 : 'Oat Milk',Value3 : 'Almond Milk'});

        console.log("here")
      }


    return ( 

    <div class = "Quiz">
    <div class ="content">
    <h1>Beanz Quiz</h1>
    </div>

    <div class = "quiz-content">
            <div class = "quiz">
                <h2>Do you prefer warm or cold drinks?</h2>
                <div class="form-check">
                    <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"></input>
                    <label class="form-check-label" for="flexRadioDefault">1 </label>
                </div> 
                <div class="form-check">
                    <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"></input>
                    <label class="form-check-label" for="flexRadioDefault1">2</label>
                </div>

                <div class="form-check">
                    <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1"></input>
                    <label class="form-check-label" for="flexRadioDefault1">3</label>
                </div>

                <div class="button-class">
                    <button type="button" class="btn btn-secondary" onClick={click}>Next</button>
                </div>

            </div>
        </div>
        </div>
    )};
}

export default Quiz;
