import Navbar from './navbar';
import './order.css';
import { useState } from 'react';
import { useHistory } from "react-router-dom";

const Order = () => {
    const [Title, settitle] = useState('Personal Information');
    const [Value1, setv1] = useState('Name');
    const [Value2, setv2] = useState('Phone Number');
    const [Value3, setv3] = useState('Email');
    const [Step, setStep] = useState(1);

    const history = useHistory();

    const handleClick = (Input) => {
        if(Step == 1) {
            setv1('Name'); 
            setv2('Phone Number'); 
            setv3('Email'); 
            setStep(1); 

        } else if (Step == 2) {
            setv1('Country'); 
            setv2('Province'); 
            setv3('City'); 
            setStep(2); 

        } else if (Step == 3) {
            setv1('Address'); 
            setv2('Postal Code'); 
            setv3('Appartment Number'); 
            setStep(3); 

        } else if (Step == 4) {
            setv1('Coffee Type'); 
            setv2('Milk'); 
            setv3('Number of Coffees'); 
            setStep(4); 

        } else if (Step == 5) {
            setv1('Additional Notes'); 
            setv2('Allergies'); 
            setv3('Complaints');
            setStep(5);       
        }

    }
    return ( 
        <div class="Order">
                <div class ="content">
                    <h1>Coffee Order</h1>
                </div>


                <div class = "order-content">
                    <div class = "order">

                        <div class = "steps">
                            <div class="button-class-circle">
                                <button type="button" class="btn btn-secondary btn-circle active-circle" onClick={() => handleClick(1)}>  Step 1</button>
                            </div>

                            <div class="button-class-circle">
                                <button type="button" class="btn btn-secondary btn-circle"  onClick={() => handleClick(2)}>  Step 2</button>
                            </div>

                            <div class="button-class-circle">
                                <button type="button" class="btn btn-secondary btn-circle"  onClick={() => handleClick(3)}>  Step 3</button>
                            </div>

                            <div class="button-class-circle">
                                <button type="button" class="btn btn-secondary btn-circle" onClick={() => handleClick(4)}>  Step 4</button>
                            </div>

                            <div class="button-class-circle">
                                <button type="button" class="btn btn-secondary btn-circle"  onClick={() => handleClick(5)}>  Step 5</button>
                            </div>

                        </div>

                        <h2>{Title}</h2>

                        <input type="text" class="form-control" placeholder={Step}></input>
                        <input type="text" class="form-control" placeholder={Step}></input>
                        <input type="text" class="form-control" placeholder={Step}></input>

                        <div class="button-class">
                            <button type="button" class="btn btn-secondary" onClick={() => handleClick(5)}>  Submit</button>
                        </div>

                    </div>
                </div>
        </div>
     );
}

export default Order;
